import React from "react";
import SearchForm from "../SearchForm";
import BookDetails from "../BookDetails";
import API from "../../utils/API";

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      saved: "",
      bookData: []
    };
    this.handleSearchClick = this.handleSearchClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(e) {
    e.preventDefault();
    this.setState({ bookInput: e.target.value });
  }

  handleSearchClick(e) {
    e.preventDefault();
    API.search(this.state.bookInput)
      .then(response => {
        this.setState({ bookData: response.data.items[0].volumeInfo });
        console.log("Search ln38: What's bookData?", this.setState);
        this.setState({ bookInput: "" });
      })
      .catch(err => {
        console.log(err);
      });
  }

  render() {
    return (
      <main>
        <SearchForm
          handleChange={this.handleChange}
          handleSearchClick={this.handleSearchClick}
        />
        <div
          className="bookResult"
          id={this.props.id ? this.props.id : null}
          style={{ display: this.state.deleted ? "none" : "block" }}
        >
          <div className="row">
            <div className="aboutBook">
              <h4>{this.props.title}</h4>
              <p>
                By: {this.props.authors ? this.props.authors.join(", ") : "N/A"}
              </p>
            </div>
            <div className="btnDiv">
              {// if link to book exists include View button else do not
              this.props.link ? (
                <a
                  href={this.props.link}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <button type="button" name="view">
                    View
                  </button>
                </a>
              ) : null}
              {// if this.props.path is "/" display save button else display Delete button
              this.props.path === "/" ? (
                <button
                  type="button"
                  name="save"
                  onClick={this.handleSaveClick}
                  disabled={this.state.saved}
                >
                  {this.state.saved ? "Saved" : "Save"}
                </button>
              ) : (
                <button
                  type="button"
                  name="Delete"
                  onClick={this.handleDeleteClick}
                  disabled={this.state.deleted}
                >
                  Delete
                </button>
              )}
            </div>
          </div>
          <div className="row">
            {this.props.img ? (
              <img
                src={
                  // if smallthubmail exists on this.props.img use that else if thumbnail exists on this.props.img use that else leave src empty
                  this.props.img.smallThumbnail
                    ? this.props.img.smallThumbnail
                    : this.props.img.thumbnail
                    ? this.props.img.thumbnail
                    : ""
                }
                alt="book cover"
              />
            ) : null}
            <p>{this.props.description ? this.props.description : "N/A"}</p>
          </div>
        </div>
      </main>
    );
  }
}

export default Search;
