import React from "react";
import BookResult from "../../components/BookResults";
// import { Link } from "react-router-dom";
// import { Col, Row, Container } from "../../components/Grid";
// import Jumbotron from "../../components/Jumbotron";
// import API from "../../utils/API";

const BookDetails = (props) => {
  // console.log("BookDetails Ln 9: contains this:", props.bookData);  
  // return (
    if (props.path === "/") {
      return (
        <div id="resultsContainer">
        <h3>Book Details:</h3>
        {props.bookData.map((book) => {
          const bookInfo = book.volumeInfo;
          return <BookResult
          title={bookInfo.title}
          authors={bookInfo.authors}
          description={bookInfo.description}
          link={bookInfo.canonicalVolumeLink}
          img={bookInfo.imageLinks}
          path={props.path}
          key={book.id} />
        })}
        </div>
        );
      }else if (props.path === "/saved") {
        if(props.savedBooks.length > 0) {
          return(
              <div id="resultsContainer">
                  <h3>Saved Books</h3>
                  {props.savedBooks.map((book) => {
                      return <BookResult
                      title={book.title}
                      authors={book.authors}
                      description={book.description}
                      link={book.link}
                      img={book.img}
                      id={book._id}
                      path={props.path}
                      key={book._id}/>
                  })}
              </div>
          );
      } else {
          return(
               <div id="resultsContainer">
                  <h3>Saved Books</h3>
                  <p>No saved books.</p>
              </div>
          );
      }
  }
}

    // const { title, author, synopsis } = props.bookData;
    // <main>
    //   {imageLinks}
    // <h5>{props.bookData.title}</h5>
    // <h6>{props.bookData.subtitle}</h6>
    // <h5>{props.bookData.authors}</h5>
    // <h5>{props.bookData.description}</h5>
    // </main>

    export default BookDetails;
